sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  if(inputs.status_code=="200"){

   var responsebody = JSON.parse(inputs.response_body);

    outputs.total = responsebody.paging.total; 


    var sharesarray=[];


          for(var i=0;i<outputs.total;i++){
         var shareobject={};
    shareobject.id = responsebody.elements[i].id;
    shareobject.title = responsebody.elements[i].content.title;
    shareobject.activity = responsebody.elements[i].activity;
    shareobject.time = responsebody.elements[i].created.time;
    shareobject.author = responsebody.elements[i].created.actor;
    shareobject.category = responsebody.elements[i].content.shareMediaCategory;

        sharesarray[i]=shareobject;

          }        

         outputs.shares =sharesarray;
  }


})(inputs, outputs);

